import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  Image,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  Linking,
} from 'react-native';
import { StackNavigationProp } from '@react-navigation/stack';
import { RouteProp } from '@react-navigation/native';
import Swiper from 'react-native-swiper';
import { Calendar } from 'react-native-calendars';
import MapView, { Marker } from 'react-native-maps';
import StripePayment from '../utils/Stripe';
import { FontAwesomeIcon } from '@fortawesome/react-native-fontawesome';
import { faBed, faBath, faHome, faCoins, faCalendar, faCar, faTools, faRuler } from '@fortawesome/free-solid-svg-icons';
import { read } from '../utils/Orderbook/read'
import { RootStackParamList } from '../Navigation/types';



type PropertyDetailScreenProps = {
  route: RouteProp<RootStackParamList, 'PropertyDetail'>;
  navigation: StackNavigationProp<RootStackParamList, 'PropertyDetail'>;
};

const PropertyDetailScreen: React.FC<PropertyDetailScreenProps> = ({ route, navigation }) => {
  const { property } = route.params;
  const [selectedDate, setSelectedDate] = useState<string | null>(null);
  const [showCalendar, setShowCalendar] = useState<boolean>(true);
  const [showStripe, setShowStripe] = useState(false);

  const openMaps = () => {
    const url = `http://maps.apple.com/?q=${property.address}`;
    Linking.openURL(url);
  };

  const onPaymentSuccess = () => {
    console.log('Payment Successful');
  };

  const onPaymentFailure = (error: string) => {
    console.error('Error in payment sheet', error);
  };

  const onBook = () => {
    setShowCalendar(false);
    setShowStripe(true);
  };


  const [housePrice, setHousePrice] = useState('');

  const fetchHousePrice = async () => {
    const price = await read('M8mOCM6JeDR5wxfhwHjCRnF1gZME87sEnImiglHeBQA');
    setHousePrice(JSON.stringify(price));
  }

  useEffect(() => {
    fetchHousePrice();
  }, []);


  return (
    <ScrollView style={styles.container}>
      <Swiper style={styles.swiperContainer} showsButtons={false} loop={false} height={300} activeDotColor='white' dotColor='grey' paginationStyle={{ bottom: 8 }}>
        {property.imageURLs.map((url: string, index: number) => (
          <View key={index} style={styles.imageContainer}>
            <Image style={styles.propertyImage} source={{ uri: url }} />
          </View>
        ))}
      </Swiper>
      <Text style={styles.propertyTitle}>{property.title}</Text>
      <Text style={styles.propertyDescription}>{property.description}</Text>
      <Text style={styles.propertyPrice}>${property.price}</Text>

      <TouchableOpacity onPress={openMaps} style={styles.addressContainer}>
        <Text style={styles.propertyAddress}>Open in maps</Text>
      </TouchableOpacity>

      <View>
        <Text>House price: {housePrice}</Text>
      </View>



      <View style={styles.navigateToNewScreenButtonContainer}>
        <TouchableOpacity 
          style={styles.navigateToNewScreenButton} 
          onPress={() => {
            navigation.navigate('TradeProperty');
          }}
        >
          <Text style={styles.navigateToNewScreenButtonText}>Go to trade Screen</Text>
        </TouchableOpacity>
      </View>


      <MapView
        style={styles.map}
        initialRegion={{
          latitude: property.coordinates.latitude,
          longitude: property.coordinates.longitude,
          latitudeDelta: 0.0922,
          longitudeDelta: 0.0421,
        }}
      >
        <Marker
          coordinate={property.coordinates}
          title={property.title}
          description={property.address}
        />
      </MapView>

      {showCalendar && (
        <Calendar
          onDayPress={(day) => setSelectedDate(day.dateString)}
          markedDates={selectedDate ? { [selectedDate]: { selected: true } } : {}}
        />
      )}

      <View style={styles.propertyDetailsContainer}>
        <View style={styles.propertyDetailRow}>
          <FontAwesomeIcon icon={faHome} style={styles.detailIcon} />
          <Text style={styles.propertyDetailLabel}>Status: {property.status}</Text>
        </View>
        <View style={styles.propertyDetailRow}>
          <FontAwesomeIcon icon={faBed} style={styles.detailIcon} />
          <Text style={styles.propertyDetailLabel}>Bedrooms: {property.bedrooms}</Text>
        </View>
        <View style={styles.propertyDetailRow}>
          <FontAwesomeIcon icon={faBath} style={styles.detailIcon} />
          <Text style={styles.propertyDetailLabel}>Bathrooms: {property.bathrooms}</Text>
        </View>
        <View style={styles.propertyDetailRow}>
          <FontAwesomeIcon icon={faRuler} style={styles.detailIcon} />
          <Text style={styles.propertyDetailLabel}>Square Footage: {property.squareFootage} sqft</Text>
        </View>
        <View style={styles.propertyDetailRow}>
          <FontAwesomeIcon icon={faCar} style={styles.detailIcon} />
          <Text style={styles.propertyDetailLabel}>Garage: {property.hasGarage ? 'Yes' : 'No'}</Text>
        </View>
        <View style={styles.propertyDetailRow}>
          <FontAwesomeIcon icon={faTools} style={styles.detailIcon} />
          <Text style={styles.propertyDetailLabel}>Basement: {property.hasBasement ? 'Yes' : 'No'}</Text>
        </View>
        <View style={styles.propertyDetailRow}>
          <FontAwesomeIcon icon={faCoins} style={styles.detailIcon} />
          <Text style={styles.propertyDetailLabel}>Price per Token: ${property.perTokenPrice}</Text>
        </View>
        <View style={styles.propertyDetailRow}>
          <FontAwesomeIcon icon={faCalendar} style={styles.detailIcon} />
          <Text style={styles.propertyDetailLabel}>Tokens Available: {property.tokensAvailable}</Text>
        </View>
      </View>

      <View style={styles.bookButtonContainer}>
        <TouchableOpacity style={styles.bookButton} onPress={onBook}>
          <Text style={styles.bookButtonText}>Book Now</Text>
        </TouchableOpacity>
      </View>

      {showStripe && <StripePayment onPaymentSuccess={onPaymentSuccess} onPaymentFailure={onPaymentFailure} />}

    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'white',
  },
  swiperContainer: {
    marginBottom: 10,
  },
  imageContainer: {
    height: 300,
    borderRadius: 8,
    overflow: 'hidden',
  },
  propertyImage: {
    width: '100%',
    height: '100%',
  },
  propertyTitle: {
    fontSize: 24,
    fontWeight: 'bold',
    marginVertical: 10,
    marginHorizontal: 10,
  },
  propertyDescription: {
    margin: 10,
    fontSize: 16,
    color: '#333',
  },
  propertyPrice: {
    fontSize: 20,
    fontWeight: 'bold',
    marginVertical: 10,
    marginHorizontal: 10,
  },
  addressContainer: {
    alignItems: 'center',
    marginVertical: 10,
  },
  propertyAddress: {
    color: 'blue',
    textDecorationLine: 'underline',
  },
  map: {
    height: 200,
    margin: 10,
    borderRadius: 8,
    overflow: 'hidden',
  },
  propertyDetailsContainer: {
    margin: 10,
  },
  propertyDetailRow: {
    flexDirection: 'row',
    alignItems: 'center',
    marginVertical: 5,
  },
  detailIcon: {
    marginRight: 10,
    fontSize: 24,
  },
  propertyDetailLabel: {
    fontSize: 16,
  },
  bookButtonContainer: {
    alignItems: 'center',
    margin: 20,
  },
  bookButton: {
    backgroundColor: '#ff5a5f',
    paddingVertical: 10,
    paddingHorizontal: 20,
    borderRadius: 5,
  },
  bookButtonText: {
    color: 'white',
    fontSize: 16,
    fontWeight: 'bold',
  },


  navigateToNewScreenButtonContainer: {
    alignItems: 'center',
    margin: 20,
  },
  navigateToNewScreenButton: {
    backgroundColor: '#4CAF50',
    paddingVertical: 10,
    paddingHorizontal: 20,
    borderRadius: 5,
  },
  navigateToNewScreenButtonText: {
    color: 'white',
    fontSize: 16,
    fontWeight: 'bold',
  },

});

export default PropertyDetailScreen;
